import React from 'react';

const Tabela = (props) => {
    return (
      <div>
        <table className="table table-striped">
                    <thead>
                        <tr>
                            <th scope="col">id_prod</th>
                            <th scope="col">Categoria</th>
                            <th scope="col">Descrição</th>
                            <th scope="col">Preço</th>
                        </tr>
                    </thead>
                    <tbody>
                {this.props.arrayProd.map(
                    row=>
                        <tr key={row.id_prod}>
                            <th scope="row">{row.id_prod}</th>
                            <td>{row.nome_prod}</td>
                            <td>{row.descricao_prod}</td>
                            <td>{row.preco_prod}</td>
                        </tr>
                )}
                    </tbody>
            </table>
      </div>
    );
  };


export default Tabela;

