import React from 'react';
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button
  } from 'reactstrap';

export default class Tabela extends React.Component{

    render(){
        return(
            <div className="container">
            <div className="row">
                <div className="col-2">
                <h3>Categorias</h3> 
                <ul>
                    <li onClick={allCategories}>Todos (10)</li> 
                    <li id="geladeiras" onClick={filterCategory}>Geladeiras (2)</li>
                    <li id="fogoes" onClick={filterCategory}>Fog√£o (2)</li> 
                    <li id="lavadoura" onClick={filterCategory}>Lavadoras (4)</li> 
                    <li id="microondas" onClick={filterCategory}>Microondas (2)</li> 
                    <li id="lavaloucas" onClick={filterCategory}>Lava-Loucas (2)</li> 
                </ul> 
                </div>
        <div className="col-10">
            <div className="d-flex flex-wrap justify-content-around">  
                {this.props.arrayProd.map(
                    row=>
                <div style={{width:"300px", height:"450px"}} key={row.id_prod}>
                    <Card>
                    <CardBody>
                    <CardImg top src={require(`${row.img_prod}`).default} alt="{row.nome_prod}" />
                    <CardTitle tag="h5">{row.descricao_prod}</CardTitle>
                    <Button color="danger">R$ {row.preco_prod}</Button>
                    <Button color="primary">Comprar</Button>

                    </CardBody>
                    </Card>
                </div>
                )}
        </div>
            </div>
            </div>
            </div>
        );
    }
}
