import React from 'react';
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button
  } from 'reactstrap';

export default class Tabela extends React.Component{

    render(){
        return(
            <div>

                {this.props.arrayProd.map(
                    row=>
                    <div style={{width:"300px", height:"600px"}} key={row.id_prod}>
        <Card>
        <CardBody>
        <CardImg top src={require(`${row.img_prod}`).default} alt="Card image cap" />
          <CardTitle tag="h5">{row.nome_prod}</CardTitle>
          <CardText>{row.descricao_prod}</CardText>
          <Button color="danger">R$ {row.preco_prod}</Button>
        </CardBody>
        </Card>
                        </div>
                )}

            </div>
        );
    }
}
